package com.hedza06.camttl.controllers;import com.hedza06.camttl.services.SmsService;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping(value = "/sms", produces = MediaType.APPLICATION_JSON_VALUE)public class SmsController {    private final SmsService smsService;    public SmsController(SmsService smsService) {        this.smsService = smsService;    }    /**     * Start process with variables     *     * @param messageText message text     * @param identifier message identifier     * @return ResponseEntity with Http Status Code (200)     */    @PostMapping(value = "/create", produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Void> startProcessWithVariables(@RequestParam(value = "messageText") String messageText,                                                          @RequestParam(value = "messageIdentifier") String identifier)    {        smsService.startProcess(messageText, identifier);        return new ResponseEntity<>(HttpStatus.CREATED);    }}