package com.hedza06.camttl.domain;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;import java.io.Serializable;import java.util.Date;@Data@Entity@Table(name = "sms")@NoArgsConstructor@AllArgsConstructorpublic class Sms implements Serializable {    @Id    @Column(updatable = false, nullable = false)    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    @Column(name = "message_text", nullable = false, length = 159)    private String messageText;    @Column(name = "message_identifier", nullable = false, length = 64)    private String messageIdentifier;    @Column(name = "created_at", nullable = false, columnDefinition = "DATE DEFAULT CURRENT_TIMESTAMP")    private Date createdAt = new Date();    public Sms(String messageText, String messageIdentifier)    {        this.messageText = messageText;        this.messageIdentifier = messageIdentifier;    }}