package com.hedza06.camttl.services;import com.hedza06.camttl.domain.Sms;import com.hedza06.camttl.repositories.SmsRepository;import org.camunda.bpm.engine.RuntimeService;import org.camunda.bpm.engine.variable.Variables;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Service@Transactionalpublic class SmsService {    private final SmsRepository smsRepository;    private final RuntimeService runtimeService;    public SmsService(SmsRepository smsRepository, RuntimeService runtimeService)    {        this.smsRepository = smsRepository;        this.runtimeService = runtimeService;    }    /**     * Start process with variables     *     * @param messageText SMS text     * @param identifier SMS identifier     */    public void startProcess(String messageText, String identifier)    {        runtimeService            .createProcessInstanceByKey("sms")            .setVariables(                Variables.createVariables()                    .putValue("messageText", messageText)                    .putValue("identifier", identifier)            ).execute();        smsRepository.save(new Sms(messageText, identifier));    }}